friends.map:
function(doc) {
		for (edge in doc.edges) {
			emit (doc._id, edge)
		}
	}

friends_of_friends.map:
function(doc) {
		for (var edge in doc.edges) {
			emit (doc._id,{_id: edge});
		}
}

friends_of_friend.map:
function(doc) {
		if(doc.edges) {
			for (var edge in doc.edges) {
				emit (doc._id,edge)
			}
		}
}

sorted_unique_friends.map:
function(doc) {
		for (var edge in doc.edges) {
			if (edge < doc._id) {a = edge; b=doc._id}
			else {b = edge; a=doc._id}

			emit ([a, b],null)
		}
}
